package com.lichkin.application.apis.api001;import java.util.List;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.lichkin.framework.beans.impl.LKRequestBean;import com.lichkin.framework.defines.LKFrameworkStatics;import com.lichkin.framework.defines.beans.impl.LKTreeBean;import com.lichkin.framework.defines.exceptions.LKException;import com.lichkin.framework.utils.LKTreeUtils;import com.lichkin.framework.web.annotations.LKApiType;import com.lichkin.framework.web.enums.ApiType;import com.lichkin.springframework.controllers.LKApiY0Controller;import com.lichkin.springframework.web.LKSession;@RestController("GetOwnMenusController")@RequestMapping(value = LKFrameworkStatics.WEB_MAPPING_API_WEB_ADMIN + "/Menus")@LKApiType(apiType = ApiType.PERSONAL_BUSINESS)public class C extends LKApiY0Controller<LKRequestBean, List<LKTreeBean>> {	@Deprecated	@Override	protected List<LKTreeBean> doInvoke(LKRequestBean cin) throws LKException {		return LKTreeUtils.toTree(LKSession.getMenus(session), "id", "menuName", "menuCode", "parentCode");	}	@Override	protected boolean saveLog(LKRequestBean cin) {		return false;	}}