package com.lichkin.application.apis.api002;import java.util.HashMap;import java.util.Map;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import com.lichkin.framework.beans.impl.LKRequestBean;import com.lichkin.framework.defines.LKFrameworkStatics;import com.lichkin.framework.defines.exceptions.LKException;import com.lichkin.framework.web.annotations.LKApiType;import com.lichkin.framework.web.enums.ApiType;import com.lichkin.springframework.controllers.LKApiY0Controller;import com.lichkin.springframework.entities.impl.SysAdminLoginEntity;@RestController("GetUserInfoController")@RequestMapping(value = LKFrameworkStatics.WEB_MAPPING_API_WEB_ADMIN + "/AccountInfo")@LKApiType(apiType = ApiType.PERSONAL_BUSINESS)public class C extends LKApiY0Controller<LKRequestBean, Map<String, Object>> {	@Override	protected Map<String, Object> doInvoke(LKRequestBean cin) throws LKException {		SysAdminLoginEntity login = (SysAdminLoginEntity) cin.getDatas().getLogin();		Map<String, Object> map = new HashMap<>();		map.put("id", login.getId());		map.put("userName", login.getUserName());		map.put("gender", login.getGender());		map.put("email", login.getEmail());		map.put("level", login.getLevel());		map.put("photo", login.getPhoto());		return map;	}	@Override	protected boolean saveLog(LKRequestBean cin) {		return false;	}}